function openCard(e,c){if(card=document.getElementById(c),card.classList.remove("bounce"),card.offsetWidth=card.offsetWidth,card.classList.add("bounce"),window.innerWidth<1024)card.parentElement.querySelectorAll(".card-indicator")[0].classList.toggle("hidden");else{cards=document.querySelectorAll(".marketing-cards > ul .card");for(let e=0;e<cards.length;++e)cards[e].className=cards[e].className.replace(" active","");peek=document.getElementsByClassName("marketing-peek")[0],peekBody=peek.querySelectorAll(".card > .card-body")[0],peekBody.style.opacity=0,setTimeout(function(){peekBody.innerHTML=card.querySelectorAll(".card-body")[0].innerHTML,peekBody.style.opacity=1},300)}card.classList.toggle("active")}